import React from "react";



// Context for storing user database
const UserContext = React.createContext(null);

const UserProvider = ({ children }) => {

  return (
    <UserContext.Provider
      value={{
            name: "",
            email: "",
            password: "",
            balance: 0,
          }
      }
    >
      {children}
    </UserContext.Provider>
  );
};

export default UserContext;
export { UserProvider };
